import { useParams } from 'react-router';
import {React, useEffect, useState,createContext } from 'react';
import Axios from 'axios';
import Comments from '../components/Comments';

export const Context=createContext()

function PostDetails() {
       const id=useParams();
       const [comments, setComments] = useState([]);
       const [posts, setPost] = useState([]);
       useEffect(() => {
        Axios.get('https://jsonplaceholder.typicode.com/posts/'+id.id)
            .then(res => {
                console.log(res.data.title);
                setPost(res.data)
            })
            .catch(err => console.log(err))

    }, []);
  
       useEffect(() => {
           Axios.get('https://jsonplaceholder.typicode.com/posts/'+id.id+'/comments')
               .then(res => {
                  //  console.log(res.data[0]);
                   setComments(res.data)})
               .catch(err => console.log(err))  
  
       }, []);

    

   return (
        <div className='center1'>
          <br/>
           <h3 className='center'>{posts.title}</h3>
           <p className='center'>{posts.body}</p>
           <hr/><hr/>
          <h1 className='center'>             
            Comments</h1>
            <ol type="1">
                <Context.Provider value="oosd">
                      <Comments/>

                </Context.Provider>
           {

        //    comments.map((data,index)=>{
        //     return(
        //          <div>
        //            <li>
        //            <p> <u>Name: </u>{data.name}</p>
        //            <p><u>Comments:</u> {data.body}</p>
        //            <hr/>
        //            </li>
        //            </div>
                
           
        //     )
        //    })
         
           }
          </ol>
        </div>
    );
}

export default PostDetails;
