import { React, useEffect, useState } from 'react';
import Axios from 'axios';

function Update() {
    const id = new URLSearchParams(window.location.search).get('id');
    const [posts, setPost] = useState([]);

    useEffect(() => {
        Axios.get('https://jsonplaceholder.typicode.com/posts/' + id)
            .then(res => {

                setPost(res.data)
                document.getElementById('utitle').value = res.data.title
                document.getElementById('ubody').value = res.data.body
            })
            .catch(err => console.log(err))

    }, []);

    const Update =()=> {
        Axios.put('https://example.com/posts/'+id, {
            title: document.getElementById('utitle').value,
            body: document.getElementById('ubody').value
        })
            .then(response => {
                console.log(response);
            })
            .catch(err => {
                console.log(err);
            });
    }

    return (
        <div className='card'>
            <p > Update {id}</p>
            <input id='utitle' />
            <textarea id='ubody' />
            <button type='submit' onClick={Update}> Update</button>
        </div>
    );
}

export default Update;
