import { React, useEffect, useState, updateState, useCallback } from 'react';
import { useHistory } from 'react-router-dom';
import Axios from 'axios'
import ReactPaginate from 'react-paginate'
import '../style.css'


function Users() {
    const history = useHistory()
    const [user, setUser] = useState([]);
    const [userName, setUserName] = useState('');
    const [emailName, setEmailName] = useState('');
    const [webSite, setWebsite] = useState('');
    const [pageNumer, setPageNumer] = useState(0);
    const [userPerPage, setUserperPage] = useState(5);


    const firstUserIndex = userPerPage * pageNumer;

    const pageCount = Math.ceil(user.length / userPerPage);

   // setUser(Axios.get('https://jsonplaceholder.typicode.com/users').data)

    useEffect(() => {
        
    }, []);


    const changePage = ({ selected }) => {
        setPageNumer(selected);
    }


    const sortName_Asc = () => {
        setUser(user.sort((a, b) => { return a.name.localeCompare(b.name) }));
        setUserperPage(user.length)

    }
    const sortName_Desc = () => {

        setUser(user.sort((a, b) => { return b.name.localeCompare(a.name) }));
        setUserperPage(user.length + 1)

    }

    const sortEmail_Asc = () => {
        setUser(user.sort((a, b) => { return a.email.localeCompare(b.email) })); setUserperPage(user.length + 2)

    }
    const sortEmail_Desc = () => {

        setUser(user.sort((a, b) => { return b.email.localeCompare(a.email) })); setUserperPage(user.length + 3)


    }

    const pageReSize = (e) => {
        e.preventDefault();
        const x = document.getElementById('input').value
        if (x != '' && x != '0') {
            setUserperPage(parseInt(x))
        }
    }


    const tableClick = (id) => {
        alert('Show Profile?')
        history.push('/userprofile/' + id);
    }

    var display = user.slice(firstUserIndex, firstUserIndex + userPerPage).map((user, index) => {
        if (user.name.match(userName) && user.email.match(emailName) && user.website.match(webSite)) {

            return (
                <tr key={index} onClick={() => tableClick(user.id)} >
                    <td>{user.name} </td>
                    <td> {user.email}</td>
                    <td> {user.website}</td>
                </tr>
            )
        }
    });

    return (
        <div className='center'>
            <h1 className='center' > All Users</h1>

            <input id='input' placeholder=" user perpage" />
            <button type="submit" onClick={pageReSize}>Enter</button>


            <table id="myTable">
                <tbody>

                    <th>Name
                        <input className="input" placeholder="Search Name" onChange={event => setUserName(event.target.value)} />
                        <label>Sort By</label>
                        <button onClick={sortName_Asc}>Asc</button>
                        <button onClick={sortName_Desc}>Desc</button>
                    </th>
                    <th>Email
                        <input placeholder="Search Email" onChange={event => setEmailName(event.target.value)} />
                        <label>Sort By</label>
                        <button onClick={sortEmail_Asc}>Asc</button>
                        <button onClick={sortEmail_Desc}>Desc</button>

                    </th>
                    <th>Website
                        <input placeholder="Search Website" onChange={event => setWebsite(event.target.value)} />
                    </th>

                    {
                        display
                    }


                </tbody>
            </table>

            <ReactPaginate
                pageCount={pageCount}
                onPageChange={changePage}
                containerClassName={"paginationStyle"}
                activeClassName={"active"}
            />

        </div>
    );
}

export default Users;
