import { React, useState, useEffect } from 'react';
import Axios from 'axios';
import ReactPaginate from 'react-paginate';

function DeletePost (id){
      Axios.delete('https://jsonplaceholder.typicode.com/posts/'+id)
    .then(res => {
        console.log(res.data);
    })
    .catch(err => console.log(err))
   alert('Sucessfully Delete');
}


function Profile() {
    const [count, setCount] = useState(10);
    const [posts, setPost] = useState([]);
    const userPerPage= 3;
    const [pageNumer, setPageNumer] = useState(0);
    const pageCount = Math.ceil(posts.length / userPerPage) 
    const firstPostIndex = userPerPage * pageNumer;

    useEffect(() => {
        Axios.get('https://jsonplaceholder.typicode.com/posts?userId=2')
            .then(res => {
                  console.log(res.data);
                setPost(res.data)
            })
            .catch(err => console.log(err))

    }, []);


    return (
        <div >
            <h1 id="center"> Profile </h1>

            <div >

                <ol type="1">

                      {                   
                      posts.slice(firstPostIndex,firstPostIndex+userPerPage).map((post, index) => {
                        //  if (post.userId == 2)
                             return(
                           
                             <li key={index}>
                                <div >
                                    <h3>  Title : {post.title} </h3>
                                    <p>Body: {post.body}</p>
                                    <button onClick={()=>DeletePost(post.id)}> Delete </button>
                                     <button > UpDate </button>
                                    <hr></hr>
                                </div>
                            </li>
                            )
                    }) 
                  
                    } 
                 
                  
                    
                    

                 
                    


                </ol>

             {posts.id}
            </div>
          
            <ReactPaginate
                pageCount={pageCount}
                // onPageChange={changePage}
                containerClassName={"paginationStyle"}
                activeClassName={"active"}
            />
        </div>
        
    )};

export default Profile;
