import { React, useEffect, useState } from 'react';
import Axios from 'axios'
import ReactPaginate from 'react-paginate'
import '../style.css'


function Users() {
    
    const [users, setUsers] = useState([]);
    const [pageNumer, setPageNumer] = useState(0);
    const userPerPage = 5;
    const firstUserIndex = userPerPage * pageNumer;
    const pageCount = Math.ceil(users.length / userPerPage)

    useEffect(() => {
        Axios.get('https://jsonplaceholder.typicode.com/users')
            .then(res => {
                setUsers(res.data)
            })
            .catch(err => console.log(err))

    }, []);

    const display =
        users.slice(firstUserIndex, firstUserIndex + userPerPage).map((user, index) => {
            return (<tr key={index}>
                <td>{user.name}</td>
                <td> {user.email}</td>
                <td> {user.website}</td>
            </tr>)
        }); 

    const changePage = ({ selected }) => {
        setPageNumer(selected);
    }

    return (
        <div>
            <h1 id='center' > All Users</h1>
            <form>
            <input onChange={event => setPageNumer(event.target.value)} />
            <button type="s"></button>
            </form>
          
            <button>Paginationa Size</button>
            <table id="myTable">
                <tbody>
                    <tr>
                        <th>Name    <button > Sort by</button></th>
                        <th>Email   <button > Sort by</button></th>
                        <th>Website</th>
                    </tr>

                    {display}

                </tbody>
            </table>


            <ReactPaginate
                pageCount={pageCount}
                onPageChange={changePage}
                containerClassName={"paginationStyle"}
                activeClassName={"active"}
            />

        </div>
    );
}

export default Users;
