import { React, useEffect, useState } from 'react';
import Axios from 'axios'
import ReactPaginate from 'react-paginate'



function Users() {
        

 
    const [users, setUsers] = useState([]);
    const [pageNumer, setPageNumer]=useState(0);
       
    const userPerPage=5;
    const firstUserNumber=userPerPage*pageNumer;


    useEffect(() => {
        Axios.get('https://jsonplaceholder.typicode.com/users')
            .then(res => {              
                setUsers(res.data)})
            .catch(err => console.log(err))

    }, []);

   const display=
         users.slice(firstUserNumber,firstUserNumber+userPerPage).map((user, index) => {                     
         return ( <tr key={index}>
           <td>{user.name}</td> 
           <td> {user.email}</td> 
           <td> {user.website}</td>                                   
      </tr> )
      });            
  

   const pageCount=Math.ceil(users.length/userPerPage)
   const  changePage=({selected})=>{
        setPageNumer(selected);
   }

    return (
        <div>
           <h1 id='center' > All Users</h1>
       
            <table id="myTable">
                <tbody>

                <tr>
                    <th>Name    <button > Sort by</button></th>
                    <th>Email   <button > Sort by</button></th>
                    <th>Website</th>
                </tr>
               
                  {display}

                </tbody>
               
                      

               

                

            </table>


              <ReactPaginate
               pageCount={pageCount}
               onPageChange={chagePage}
            />

        </div>
    );
}

export default Users;
