import { React, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import Axios from 'axios'
import ReactPaginate from 'react-paginate'
import '../style.css'


function Users() {
    const history = useHistory()
    const [user, setUser] = useState([]);
    const [userName, setUserName] = useState('');
    const [emailName, setEmailName] = useState('');
    const [webSite, setWebsite] = useState('');
    const [pageNumer, setPageNumer] = useState(0);
    const [userPerPage, setUserperPage] = useState(5);

    const firstUserIndex = userPerPage * pageNumer;

    const pageCount = Math.ceil(user.length / userPerPage);

    useEffect(() => {
        Axios.get('https://jsonplaceholder.typicode.com/users')
            .then(res => {
                 setUser(res.data)
            })
            .catch(err => console.log(err))
 }, []);


    const changePage = ({ selected }) => {
        setPageNumer(selected);
    }


    const sortAsc = () => {          
        setUser(user.sort((a, b) => { return a.name.localeCompare(b.name) }));     
        setUserperPage(user.length)       

    }

    const sortDesc = () => {          
        setUser(user.sort((a, b) => { return b.name.localeCompare(a.name) }));     
        setUserperPage(user.length)       

    }
    const pageReSize = (e) => {
        e.preventDefault();
        const x = document.getElementById('input').value
        if (x !=''&& x!='0') {
            setUserperPage(parseInt(x))          
        }
    }
     const tableClick=(id)=>{
         alert('Show Profile?')
        history.push('/profile/'+id);
     }

    var display =user.slice(firstUserIndex, firstUserIndex + userPerPage).map((user, index) => {
            if (user.name.match(userName) && user.email.match(emailName) && user.website.match(webSite))           
             {

                return (
                    <tr key={index} onClick={()=>tableClick(user.id)} >
                        <td>{user.name} </td>
                        <td> {user.email}</td>
                        <td> {user.website}</td>
                    </tr>
                    )
            }
        });

    return (
        <div>
            <h1 className='center' > All Users</h1>

            <input id='input' />
            <button type="submit" onClick={pageReSize}>Users per Page</button>
            

           <table id="myTable">
                <tbody>
                  
                        <th>Name  <input placeholder="Search Name"  onChange={event => setUserName(event.target.value)} />
                        <button onClick={sortAsc}>Asc</button> </th>
                        <th>Email <input  placeholder="Search Email" onChange={event => setEmailName(event.target.value)} /> </th>
                        <th>Website <input  placeholder="Search Website" onChange={event => setWebsite(event.target.value)} /> </th>                   

                    {
                          display
                    }


                </tbody>
            </table>

             <ReactPaginate
                pageCount={pageCount}
                onPageChange={changePage}
                containerClassName={"paginationStyle"}
                activeClassName={"active"}
            />

        </div>
    );
}

export default Users;
