import { React, useEffect, useState ,useMemo} from 'react';
import Axios from 'axios'
import ReactPaginate from 'react-paginate'
import '../style.css'


function Users() {
    
    const [users, setUsers] = useState([]);
    const [userName, setUserName] = useState('');
    const [emailName, setemailName] = useState('');
    const [pageNumer, setPageNumer] = useState(0);
    const [ userPerPage,setUserperPage] =useState(5);
    const firstUserIndex = userPerPage * pageNumer;
    const pageCount = Math.ceil(users.length / userPerPage)

    useEffect(() => {
        Axios.get('https://jsonplaceholder.typicode.com/users')
            .then(res => {
                setUsers(res.data)
            })
            .catch(err => console.log(err))

    }, []);


    
   
    var display =
        users.slice(firstUserIndex, firstUserIndex + userPerPage).map((user, index) => {
            if(user.name.match(userName) && user.email.match(emailName)){
               var n=user.name;
             
             return (            
             <tr key={index}>
                <td>{user.name}</td>
                <td> {user.email}</td>
                <td> {user.website}</td>
            </tr>)
            }
        }); 

    const changePage = ({ selected }) => {
        setPageNumer(selected);
      
    }

    
     const Asc=(event)=>{ 
       event.preventDefault();    
      setUsers(users.sort((a,b)=>{return a.name.localeCompare(b.name)})); 
      console.log(users)  
   
       
     }
       const setPageSize=(e)=>
       {
          e.preventDefault();
           const x=document.getElementById('ok').value
           if(x!=null)
            setUserperPage(x)
            const pageCount = Math.ceil(users.length / userPerPage)
       }
   
    return (
        <div>
            <h1 id='center' > All Users</h1>
            <form>
            <input id='ok'  />
            <button   type="submit" onClick={setPageSize}>User per Page</button>
            </form>          
      
            <table id="myTable">
                <tbody>
                    <tr>                      
                        <th>Name  <input onChange={event => setUserName(event.target.value)}/> 
                         <button onClick={Asc}>Asc</button></th>
                        <th>Email <input onChange={event => setemailName(event.target.value)}/></th>
                        <th>Website</th>

                    </tr>

                    {
                    display                   
                    }
                    

                </tbody>
            </table>

             
            <ReactPaginate
                pageCount={pageCount}
                onPageChange={changePage}
                containerClassName={"paginationStyle"}
                activeClassName={"active"}
            />

        </div>
    );
}

export default Users;
