function PostDescription() {
       const id=useParams();
       const [data,setData]=useState([]);
     Axios.get('https://jsonplaceholder.typicode.com/posts/'+id.id+"/comments")
            .then(res => {
              
                data=res.data
                console.log(data);
                setData(res.data)
            })
            .catch(err => console.log(err))

    

   return (
        <div>
          <h1>             
            helllo
           {data.id}
          </h1>
        </div>
    );
}

export default PostDescription;
