import { React, useEffect, useState ,useMemo} from 'react';
import Axios from 'axios'
import ReactPaginate from 'react-paginate'
import '../style.css'


function Users() {
    
    const [users, setUsers] = useState([]);
    const [userName, setUserName] = useState('');
    const [emailName, setemailName] = useState('');
    const [pageNumer, setPageNumer] = useState(0);
    var userPerPage = 5;
    const firstUserIndex = userPerPage * pageNumer;
    const pageCount = Math.ceil(users.length / userPerPage)

    useEffect(() => {
        Axios.get('https://jsonplaceholder.typicode.com/users')
            .then(res => {
                setUsers(res.data)
            })
            .catch(err => console.log(err))

    }, []);


    
   useEffect(()=>{

   })
   

    const changePage = ({ selected }) => {
        setPageNumer(selected);
    }

    
     const Asc=()=>{  
     
      setUsers(users.sort((a,b)=>{return a.name.localeCompare(b.name)})); 
      console.log(users) 
     
     
    
     }

   
    return (
        <div>
            <h1 id='center' > All Users</h1>
            <form>
            <input onChange={event => setPageNumer(event.target.value)} />
            <button type="submit">Pagination Size</button>
            </form>          
      
            <table id="myTable">
                <tbody>
                    <tr>                      
                        <th>Name  <input onChange={event => setUserName(event.target.value)}/> 
                         <button onClick={Asc}>Asc</button></th>
                        <th>Email <input onChange={event => setemailName(event.target.value)}/></th>
                        <th>Website</th>

                    </tr>

                    {display 
                  
                    }
                    

                </tbody>
            </table>

             
            <ReactPaginate
                pageCount={pageCount}
                onPageChange={changePage}
                containerClassName={"paginationStyle"}
                activeClassName={"active"}
            />

        </div>
    );
}

export default Users;
