import React from 'react';
import { Context } from '../Pages/PostDetails';
function Comments() {
    return (
        <div>
                 <Context.Consumer>
                    {
                       value=>value.map((data,index)=>{
                            return(
                                 <div>
                                   <li>
                                   <p> <u>Name: </u>{data.name}</p>
                                   <p><u>Comments:</u> {data.body}</p>
                                   <hr/>
                                   </li>
                                   </div>                                
                           
                            )
                           })                       
         

                    
                    }

                 </Context.Consumer>
        </div>
    );
}

export default Comments;
