function PostDescription() {
       const id=useParams();
       const [posts, setPost] = useState([]);

       useEffect(() => {
           Axios.get('https://jsonplaceholder.typicode.com/posts/'+id.id+'/comments')
               .then(res => {
                  //  console.log(res.data[0]);
                   setPost(res.data)
               })
               .catch(err => console.log(err))
               console.log(posts[0]);
       }, []);

    

   return (
        <div>
          <h1>             
            helllo
           {
           posts.map()
         
           }
          </h1>
        </div>
    );
}

